import React, { useState, useEffect } from 'react';
import BookItem from '../../components/Books/BookItem/BookItem';
import BestRateBooks from '../../components/Books/BestRatedBooks/BestRatedBooks';
import Banner from '../../images/library.png';
import styles from './Home.module.css';
import { getBooks } from '../../lib/common';

function Home() {
  const [books, setBooks] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedTab, setSelectedTab] = useState('latest'); // 'latest' ou 'bestRated'

  useEffect(() => {
    async function getBooksList() {
      const data = await getBooks();
      if (data) {
        setBooks(data);
        setLoading(false);
      }
    }
    getBooksList();
  }, []);

  const handleTabClick = (tab) => {
    setSelectedTab(tab);
  };

  const displayBooks = () =>
    books ? books.map((book) => <BookItem size={2} book={book} key={book.id} />) : <h1>Vide</h1>;

  const backgroundImageStyle = { backgroundImage: `url(${Banner})` };

  return (
    <div className={styles.Home}>
      <div className={styles.banner} style={backgroundImageStyle} />
      <main className={styles.main}>
        <header className={styles.head}>
          <div className={styles.menu}>
            <p
              className={selectedTab === 'latest' ? styles.active : styles.inactive}
              onClick={() => handleTabClick('latest')}
            >
              Dernier ajout
            </p>
            <p
              className={selectedTab === 'bestRated' ? styles.active : styles.inactive}
              onClick={() => handleTabClick('bestRated')}
            >
              Mieux not√©
            </p>
          </div>
          <aside className={styles.offer}>
            {selectedTab === 'bestRated' && <BestRateBooks />}
          </aside>
        </header>
        <section className={styles.bookList}>
          {loading ? <h1>Chargement</h1> : selectedTab === 'latest' ? displayBooks() : null}
        </section>
      </main>
    </div>
  );
}

export default Home;
