import React, { useState } from 'react';
import PropTypes from 'prop-types'; // ✅ Ajout des PropTypes
import styles from './DropdownYear.module.css';

function DropdownYear({ onFilterChange }) {
  const [year, setYear] = useState('');
  const [yearStart, setYearStart] = useState('');
  const [yearEnd, setYearEnd] = useState('');

  const handleYearChange = (e) => {
    const newYear = e.target.value;
    setYear(newYear);
    onFilterChange(newYear);
  };

  const handleYearRangeChange = (start, end) => {
    setYearStart(start);
    setYearEnd(end);
    onFilterChange({ start, end });
  };

  return (
    <div className={styles.dropdownContainer}>
      <button
        className={styles.dropdownButton}
        type="button"
      >
        Années
      </button>
      <input
        type="number"
        value={year}
        onChange={handleYearChange}
        placeholder="Année"
      />
      <input
        type="number"
        value={yearStart}
        onChange={(e) => handleYearRangeChange(e.target.value, yearEnd)}
        placeholder="Début"
      />
      <input
        type="number"
        value={yearEnd}
        onChange={(e) => handleYearRangeChange(yearStart, e.target.value)}
        placeholder="Fin"
      />
    </div>
  );
}

// ✅ Ajout de la validation des props
DropdownYear.propTypes = {
  onFilterChange: PropTypes.func.isRequired, // Vérifie que c'est bien une fonction obligatoire
};

export default DropdownYear;
