import React, { useState, useEffect } from 'react';
import BookItem from '../../components/Books/BookItem/BookItem';
import BestRateBooks from '../../components/Books/BestRatedBooks/BestRatedBooks';
import LastBook from '../../components/Books/LastBook/LastBook';
import DropdownMenu from '../../components/DropdownMenu/DropdownMenu';
import DropdownYear from '../../components/DropdownYear/DropdownYear';
import Banner from '../../images/library.png';
import styles from './Home.module.css';
import { getBooks } from '../../lib/common';

function Home() {
  const [books, setBooks] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedTab, setSelectedTab] = useState('LastBook'); // 'LastBook' ou 'bestRated'

  // État pour les filtres
  const [selectedCategories, setSelectedCategories] = useState([]); // Liste des catégories choisies
  const [selectedYear, setSelectedYear] = useState(null); // Année sélectionnée ou plage d'années

  useEffect(() => {
    async function getBooksList() {
      const data = await getBooks();
      if (data) {
        setBooks(data);
        setLoading(false);
      }
    }
    getBooksList();
  }, []);

  const handleTabClick = (tab) => {
    setSelectedTab(tab);
  };

  const handleCategoryFilterChange = (categories) => {
    setSelectedCategories(categories);
  };

  const handleYearFilterChange = (year) => {
    setSelectedYear(year);
  };

  const displayBooks = () => {
    if (!books) {
      return <h1>Vide</h1>;
    }
    return books.map((book) => <BookItem size={2} book={book} key={book.id} />);
  };

  const backgroundImageStyle = { backgroundImage: `url(${Banner})` };

  return (
    <div className={styles.Home}>
      <div className={styles.banner} style={backgroundImageStyle} />
      <main className={styles.main}>
        <header className={styles.head}>
          <div className={styles.menu}>
            <button
              type="button"
              className={selectedTab === 'LastBook' ? styles.active : styles.inactive}
              onClick={() => handleTabClick('LastBook')}
            >
              Dernier ajout
            </button>
            <button
              type="button"
              className={selectedTab === 'bestRated' ? styles.active : styles.inactive}
              onClick={() => handleTabClick('bestRated')}
            >
              Mieux noté
            </button>
          </div>
          <aside className={styles.offer}>
            {selectedTab === 'bestRated' ? <BestRateBooks /> : <LastBook />}
          </aside>
        </header>

        {/* Section Filtres */}
        <section className={styles.filters}>
          <h3>Filtres appliqués :</h3>
          <div className={styles.selectedFilters}>
            {selectedCategories.length > 0 && (
              <p>
                Catégories :
                {selectedCategories.join(', ')}
              </p>
            )}
            {selectedYear && (
              <p>
                Année :
                {typeof selectedYear === 'string' ? selectedYear : `${selectedYear.start} - ${selectedYear.end}`}
              </p>
            )}
          </div>
          {/* Composants Dropdown */}
          <DropdownMenu categories={['Fantasy', 'Science-fiction', 'Policier', 'Historique']} onFilterChange={handleCategoryFilterChange} />
          <DropdownYear onFilterChange={handleYearFilterChange} />
        </section>

        {/* Section Tous les livres */}
        <section className={styles.bookList}>
          {loading ? <h1>Chargement...</h1> : displayBooks()}
        </section>
      </main>
    </div>
  );
}

export default Home;
