import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import * as PropTypes from 'prop-types';
import styles from './Header.module.css';
import Logo from '../../images/WonderBook.png';

function Header({ user, setUser }) {
  const navigate = useNavigate();
  const disconnect = () => {
    localStorage.clear();
    setUser(null);
    navigate('/');
  };

  return (
    <header className={styles.Header}>
      <div className={styles.container}>
        <img src={Logo} alt="logo WonderBook" />
        <ul>
          <li>
            <NavLink to="/" end className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
              Accueil
            </NavLink>
          </li>
          <li>
            <NavLink to="/Ajouter" className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
              Catégories
            </NavLink>
          </li>
          <li>
            <NavLink to="/Ajouter" className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
              Années
            </NavLink>
          </li>
          <li>
            <NavLink to="/Ajouter" className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
              Forum
            </NavLink>
          </li>
          <li>
            <NavLink to="/Ajouter" className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
              Ma collection
            </NavLink>
          </li>
          <li>
            <button type="button" onClick={!user ? () => navigate('/Connexion') : disconnect} onKeyUp={!user ? () => navigate('/Connexion') : disconnect} className={styles.activeLink} aria-label={!user ? 'Se connecter' : 'Se déconnecter'}>
              {!user ? 'Se connecter' : 'Se déconnecter'}
            </button>
          </li>
        </ul>
        <div cl>
          <div>
            <input type="text" />
            <button type="button">
              <span>
                Recherche
              </span>
            </button>
          </div>
        </div>
        <div className={styles.Button}>
          <button type="button" onClick={!user ? () => navigate('/Connexion') : disconnect} onKeyUp={!user ? () => navigate('/Connexion') : disconnect} className={styles.activeLink} aria-label={!user ? 'Se connecter' : 'Se déconnecter'}>
            {!user ? 'Se connecter' : 'Se déconnecter'}
          </button>
          <button type="button" onClick={!user ? () => navigate('/Connexion') : disconnect} onKeyUp={!user ? () => navigate('/Connexion') : disconnect} className={styles.activeLink} aria-label={!user ? 'Se connecter' : 'Se déconnecter'}>
            {!user ? 'Se connecter' : 'Se déconnecter'}
          </button>
        </div>
      </div>
    </header>
  );
}

Header.propTypes = {
  user: PropTypes.shape({
    userId: PropTypes.string,
    token: PropTypes.string,
  }),
  setUser: PropTypes.func.isRequired,
};

Header.defaultProps = {
  user: null,
};

export default Header;
