import React, { useState } from 'react';
import Banner from '../../images/library.png';
import Avatar from '../../images/avatar.png';
import FeatherIcon from '../../images/feather.png';
import styles from './Account.module.css';

function Account() {
  const backgroundImageStyle = { backgroundImage: `url(${Banner})` };
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [isSwitchOn, setIsSwitchOn] = useState(false); // État du switch

  const toggleSwitch = () => {
    setIsSwitchOn((prevState) => !prevState); // Inversion de l'état
  };

  return (
    <div className={styles.account}>
      <div className={styles.banner} style={backgroundImageStyle} />
      <main className={styles.main}>
        <button type="button" className={styles.Button} aria-label="edit">
          Editer Profile
          <img src={FeatherIcon} alt="Feather Icon" className={styles.icon} />
        </button>
        <section className={styles.info}>
          <article>
            <label htmlFor="username">
              <p>Pseudo</p>
              <input
                type="text"
                name="username"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </label>

            <label htmlFor="email">
              <p>E-mail</p>
              <input
                type="email"
                name="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </label>
          </article>
          <aside>
            <img src={Avatar} alt="" />
          </aside>
        </section>
        <hr />
        <section className={styles.notif}>
          <h4>Notifications</h4>
          <article>
            <div className={styles.switch_container}>
              <p>Réponse Forum</p>
              <label htmlFor="switch" className={styles.switch}>
                <input
                  type="checkbox"
                  checked={isSwitchOn}
                  onChange={toggleSwitch}
                />
                <span className={styles.slider} />
              </label>
            </div>
          </article>
        </section>
        <hr />
        <section className={styles.pres}>
          <h4>h4</h4>
          <article>article</article>
        </section>
      </main>
    </div>
  );
}

export default Account;
