import React from 'react';
import styles from './Home.module.css';

function Home({ selectedCategories, selectedYear }) {
  return (
    <div className={styles.Home}>
      <main className={styles.main}>
        {/* Section des Filtres sélectionnés */}
        <section className={styles.filters}>
          <h3>Filtres appliqués :</h3>
          <div className={styles.selectedFilters}>
            {selectedCategories.length > 0 && (
              <p>
                <strong>
                Catégories :</strong> {selectedCategories.join(', ')}</p>
            )}
            {selectedYear && (
              <p>
                <strong>
                Année :</strong> {typeof selectedYear === 'string' ? selectedYear : `${selectedYear.start} - ${selectedYear.end}`}</p>
            )}
          </div>
        </section>
      </main>
    </div>
  );
}

export default Home;
