import React, { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import SignIn from './pages/SignIn/SignIn';
import Login from './pages/Login/Login';
import Home from './pages/Home/Home';
import Book from './pages/Book/Book';
import { APP_ROUTES } from './utils/constants';
import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import AddBook from './pages/AddBook/AddBook';
import UpdateBook from './pages/updateBook/UpdateBook';
import { useUser } from './lib/customHooks';
import ScrollToTop from './components/ScrollToTop/ScrollToTop';
import Modal from './components/Modals/Modal';

function App() {
  const [user, setUser] = useState(null);
  const { connectedUser } = useUser();

  useEffect(() => {
    setUser(connectedUser);
  }, [connectedUser]);

  const [isSignInOpen, setSignInOpen] = useState(false);
  const [isLoginOpen, setLoginOpen] = useState(false);

  return (
    <BrowserRouter>
      <ScrollToTop />
      <Header
        user={user}
        setUser={setUser}
        onSignInClick={() => setSignInOpen(true)}
        onLoginClick={() => setLoginOpen(true)}
      />
      {/* Modals */}
      <Modal isOpen={isSignInOpen} onClose={() => setSignInOpen(false)}>
        <SignIn
          setUser={setUser}
          isModal
          onClose={() => setSignInOpen(false)}
          onLoginClick={() => setLoginOpen(true)}
        />
      </Modal>

      <Modal isOpen={isLoginOpen} onClose={() => setLoginOpen(false)}>
        <Login setUser={setUser} isModal onClose={() => setLoginOpen(false)} />
      </Modal>

      {/* Routes classiques */}
      <Routes>
        <Route index element={<Home />} />
        <Route path={APP_ROUTES.SIGN_IN} element={<SignIn setUser={setUser} />} />
        <Route path={APP_ROUTES.LOGIN} element={<Login setUser={setUser} />} />
        <Route path={APP_ROUTES.BOOK} element={<Book />} />
        <Route path={APP_ROUTES.UPDATE_BOOK} element={<UpdateBook />} />
        <Route path={APP_ROUTES.ADD_BOOK} element={<AddBook />} />
      </Routes>

      <Footer />
    </BrowserRouter>
  );
}

export default App;
