import React from 'react';
import PropTypes from 'prop-types';
import styles from './Modal.module.css';

function Modal({ isOpen, onClose, children }) {
  if (!isOpen) return null;

  const handleBackdropClick = (e) => {
    if (e.target.classList.contains(styles.ModalBackdrop)) {
      onClose();  // Ferme la modal si on clique sur l'arrière-plan
    }
  };

  return (
    <div
      className={styles.ModalBackdrop}
      onClick={handleBackdropClick}
      role="button"          // Ajout du rôle pour l'accessibilité
      tabIndex={0}           // Rendre le backdrop focusable via le clavier
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          onClose();         // Permet de fermer la modal avec Entrée ou Espace
        }
      }}
      aria-label="Fermer la modal"
    >
      <div
        className={styles.ModalContent}
        onClick={(e) => e.stopPropagation()}  // Empêche la fermeture lorsqu'on clique à l'intérieur
        role="dialog"                         // Définit que c'est une boîte de dialogue
        aria-modal="true"
        tabIndex={-1}                         // Focusable pour les lecteurs d'écran
        onKeyDown={(e) => {
          if (e.key === 'Escape') {
            onClose();
          }
        }}
      >
        <button
          type="button"
          onClick={onClose}
          className={styles.CloseButton}
          aria-label="Fermer la modal"
        >
          X
        </button>
        {children}
      </div>
    </div>
  );
}

Modal.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
  children: PropTypes.node.isRequired,
};

export default Modal;
