import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { useNavigate } from 'react-router-dom';
import { API_ROUTES, APP_ROUTES } from '../../utils/constants';
import { useUser } from '../../lib/customHooks';
import { storeInLocalStorage } from '../../lib/common';
import GenreSelector from '../../components/GenreSelector/GenreSelector';
import styles from './AddBook.module.css';
import UploadIcon from '../../images/photo.png';

function AddBook({ setUser, onClose }) {
  const navigate = useNavigate();
  const { user, authenticated } = useUser();

  useEffect(() => {
    if (user || authenticated) {
      navigate(APP_ROUTES.DASHBOARD);
    }
  }, [user, authenticated, navigate]);

  const [author, setAuthor] = useState('');
  const [title, setTitle] = useState('');
  const [year, setYear] = useState('');
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [notification, setNotification] = useState({ error: false, message: '' });

  const Addbook = async () => {
    try {
      setIsLoading(true);
      const response = await axios.post(API_ROUTES.ADD_BOOK, {
        title,
        author,
        year,
        genres: selectedGenres,
      });
      if (!response?.data?.token) {
        setNotification({ error: true, message: 'Une erreur est survenue' });
      } else {
        storeInLocalStorage(response.data.token, response.data.userId);
        setUser(response.data);
        if (onClose) {
          onClose();
        }
        navigate('/RegisterSend');
      }
    } catch (err) {
      setNotification({ error: true, message: err.message });
    } finally {
      setIsLoading(false);
    }
  };

  const errorClass = notification.error ? styles.Error : null;
  const categories = ['Amour', 'Aventure', 'Fantastique', 'Fantasy', 'Historique', 'Policier', 'Science-Fiction'];
  return (
    <div
      className={styles.AddBook}
      onClick={(e) => {
        if (e.target === e.currentTarget) {
          if (onClose) {
            onClose();
          } else {
            navigate('/');
          }
        }
      }}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          if (onClose) {
            onClose();
          } else {
            navigate('/');
          }
        }
      }}
    >
      <div className={`${styles.Notification} ${errorClass}`}>
        {notification.message.length > 0 && <p>{notification.message}</p>}
      </div>

      <div className={styles.Form}>
        <h2>Ajouter un livre</h2>
        <section className={styles.FormContainer}>
          <article className={styles.FormProperty}>

            <label htmlFor="author">
              <p>Auteur</p>
              <input
                type="text"
                name="Auteur"
                id="author"
                value={author}
                onChange={(e) => setAuthor(e.target.value)}
              />
            </label>

            <label htmlFor="title">
              <p>Titre</p>
              <input
                type="text"
                name="Titre"
                id="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </label>

            <label htmlFor="year">
              <p>Année</p>
              <input
                type="number"
                name="Année"
                id="year"
                value={year}
                onChange={(e) => setYear(e.target.value)}
              />
            </label>
            <GenreSelector
            categories={categories}
              onSelectGenres={(selectedGenres) => setSelectedGenres(selectedGenres)} />
            {selectedGenres.length > 0 && (
              <div className={styles.selectedGenres}>
                <strong>Genres sélectionnés:</strong>
                {selectedGenres.join(', ')}
              </div>
            )}

          </article>

          <aside className={styles.FormImage}>
            <label htmlFor="attachment" className={styles.ImageUpload}>
              <p className={styles.FormatText}>Formats d’image acceptés</p>
              <p className={styles.FormatTypes}>PNG, JPEG, WEBP</p>
              <div className={styles.ImageContainer}>
                <img src={UploadIcon} alt="Upload Icon" className={styles.UploadIcon} />
              </div>
              <input
                type="file"
                id="attachment"
                className={styles.FileInput}
                accept=".png, .jpeg, .jpg, .webp"
              />
            </label>
          </aside>
        </section>

        <div className={styles.Submit}>
          <button type="button" onClick={onClose || (() => navigate('/RegisterSend'))} className={styles.Cancel}>
            <span>Annuler</span>
          </button>
          <button type="button" onClick={Addbook} className={styles.Validate} disabled={isLoading}>
            {isLoading ? 'Chargement...' : <span>Valider</span>}
          </button>
        </div>
      </div>
    </div>
  );
}

AddBook.propTypes = {
  setUser: PropTypes.func.isRequired,
  onClose: PropTypes.func,
};

AddBook.defaultProps = {
  onClose: null,
};

export default AddBook;
