import React from 'react';
import PropTypes from 'prop-types';
import styles from './Modal.module.css';

function Modal({ isOpen, onClose, children }) {
  if (!isOpen) return null;

  const handleBackdropClick = (e) => {
    if (e.target.classList.contains(styles.ModalBackdrop)) {
      onClose(); // Ferme la modal si on clique à l'extérieur
    }
  };

  return (
    <div
      className={styles.ModalBackdrop}
      onClick={handleBackdropClick}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          onClose(); // Ferme la modal avec Entrée ou Espace
        }
      }}
    >
      <div
        className={styles.ModalContent}
        onClick={(e) => e.stopPropagation()}
        role="dialog"
        aria-modal="true"
      >
        <button type="button" onClick={onClose} className={styles.CloseButton}>X</button>
        {children}
      </div>
    </div>
  );
}

Modal.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
  children: PropTypes.node.isRequired,
};

export default Modal;
