import React, { useState } from 'react';
import axios from 'axios';
import * as PropTypes from 'prop-types';
import { NavLink, useNavigate } from 'react-router-dom';
import { API_ROUTES, APP_ROUTES } from '../../utils/constants';
import { useUser } from '../../lib/customHooks';
import { storeInLocalStorage } from '../../lib/common';
import styles from './RegisterSend.module.css';

function RegisterSend({ setUser }) {
  const navigate = useNavigate();
  const { user, authenticated } = useUser();

  if (user || authenticated) {
    navigate(APP_ROUTES.DASHBOARD);
  }

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [notification, setNotification] = useState({ error: false, message: '' });

  const handleClickOutside = (event) => {
    if (event.target.classList.contains(styles.RegisterSend)) {
      navigate('/');
    }
  };

  const errorClass = notification.error ? styles.Error : null;

  return (
    <div
      className={styles.RegisterSend}
      onClick={handleClickOutside}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          handleClickOutside(e);
        }
      }}
    >
      <div className={`${styles.Notification} ${errorClass}`}>
        {notification.message.length > 0 && <p>{notification.message}</p>}
      </div>
      <div className={styles.Form}>
        <h2>S&apos;identifier</h2>
        <label htmlFor={email}>
          <p>Adresse email</p>
          <input
            type="text"
            name="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>
        <label htmlFor={email}>
          <p>Adresse email</p>
          <input
            type="text"
            name="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>
        <label htmlFor="password">
          <p>Mot de passe</p>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <label htmlFor="password">
          <p>Mot de passe</p>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <div className={styles.Option}>
          <NavLink to="/Login" end className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
            Déjà un compte ?
            <br />
            Connecter-vous
          </NavLink>
        </div>
        <div className={styles.Submit}>
          <button type="button" onClick={() => navigate('/')} className={styles.Cancel}>
            <span>Annuler</span>
          </button>
          <button type="submit" onClick={RSend} className={styles.Validate}>
            {isLoading ? <div className="" /> : null}
            <span>Valider</span>
          </button>
        </div>
      </div>
    </div>
  );
}

RegisterSend.propTypes = {
  setUser: PropTypes.func.isRequired,
};

export default RegisterSend;
