import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { NavLink, useNavigate } from 'react-router-dom';
import { API_ROUTES, APP_ROUTES } from '../../utils/constants';
import { useUser } from '../../lib/customHooks';
import { storeInLocalStorage } from '../../lib/common';
import styles from './SignIn.module.css';

function SignIn({ setUser, isModal = false, onClose }) {
  const navigate = useNavigate();
  const { user, authenticated } = useUser();

  useEffect(() => {
    if (user || authenticated) {
      if (isModal && onClose) {
        onClose();
      } else {
        navigate(APP_ROUTES.DASHBOARD);
      }
    }
  }, [user, authenticated, isModal, onClose, navigate]);

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [notification, setNotification] = useState({ error: false, message: '' });

  const handleClickOutside = (event) => {
    if (isModal && event.target.classList.contains(styles.SignIn)) {
      onClose();
    }
  };

  const signIn = async () => {
    try {
      setIsLoading(true);
      const response = await axios.post(API_ROUTES.SIGN_IN, { email, password });
      if (!response?.data?.token) {
        setNotification({ error: true, message: 'Une erreur est survenue' });
      } else {
        storeInLocalStorage(response.data.token, response.data.userId);
        setUser(response.data);
        if (isModal && onClose) {
          onClose();
        } else {
          navigate('/');
        }
      }
    } catch (err) {
      setNotification({ error: true, message: err.message });
    } finally {
      setIsLoading(false);
    }
  };

  const errorClass = notification.error ? styles.Error : '';

  return (
    <div
      className={styles.SignIn}
      onClick={handleClickOutside}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          handleClickOutside(e);
        }
      }}
    >
      <div className={`${styles.Notification} ${errorClass}`}>
        {notification.message && <p>{notification.message}</p>}
      </div>
      <div className={styles.Form}>
        <h2>S&apos;identifier</h2>
        <label htmlFor="email">
          <p>Adresse email</p>
          <input
            type="text"
            name="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>
        <label htmlFor="password">
          <p>Mot de passe</p>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <div className={styles.Option}>
          <NavLink to={APP_ROUTES.SIGN_IN} end className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>Déjà un compte ?</NavLink>
        </div>
        <div className={styles.Submit}>
          <button type="button" onClick={isModal ? onClose : () => navigate('/')} className={styles.Cancel}>
            <span>Annuler</span>
          </button>
          <button type="submit" onClick={signIn} className={styles.Validate} disabled={isLoading}>
            {isLoading ? 'Chargement...' : 'Valider'}
          </button>
        </div>
      </div>
    </div>
  );
}

SignIn.propTypes = {setUser: PropTypes.func.isRequired,
  isModal: PropTypes.bool,
  onClose: PropTypes.func,
};

export default SignIn;
