import React, { useState } from 'react';
import Banner from '../../images/library.png';
import Avatar from '../../images/avatar.png';
import FeatherIcon from '../../images/feather.png';
import ChangePass from '../../Modals/ChangePass/ChangePass';
import styles from './Account.module.css';

function Account() {
  const [showChangePass, setShowChangePass] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');

  const toggleEdit = () => {
    setIsEditing((prevState) => !prevState);
  };

  return (
    <div className={styles.account}>
      <div className={styles.banner} style={{ backgroundImage: `url(${Banner})` }} />
      <main className={styles.main}>
        <div className={styles.buttonContainer}>
          <button type="button" className={styles.Button} onClick={toggleEdit} aria-label="edit">
            {isEditing ? 'Terminer' : 'Éditer Profil'}
            <img src={FeatherIcon} alt="Feather Icon" className={styles.icon} />
          </button>

          {isEditing && (
            <button
              type="button"
              className={styles.Button}
              onClick={() => setShowChangePass(true)}
            >
              Changer le mot de passe
              <img src={FeatherIcon} alt="Feather Icon" className={styles.icon} />
            </button>
          )}
        </div>

        <section className={styles.info}>
          <article>
            <label htmlFor="username">
              <p>Pseudo</p>
              <input type="text" id="username" value={username} onChange={(e) => setUsername(e.target.value)} disabled={!isEditing} />
            </label>

            <label htmlFor="email">
              <p>E-mail</p>
              <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} disabled={!isEditing} />
            </label>
          </article>
          <aside>
            <label htmlFor="attachment" className={styles.ImageUpload}>
              <div className={styles.ImageContainer}>
                <img src={Avatar} alt="Upload Icon" className={styles.UploadIcon} />
                <p className={styles.FormatText}>Formats d’image acceptés</p>
                <p className={styles.FormatTypes}>PNG, JPEG, WEBP</p>
              </div>
              <input type="file" id="attachment" className={styles.FileInput} accept=".png, .jpeg, .jpg, .webp" disabled={!isEditing} />
            </label>
          </aside>
        </section>
      </main>

      {/* Affichage de la modal uniquement si `showChangePass` est vrai */}
      {showChangePass && <ChangePass onClose={() => setShowChangePass(false)} />}
    </div>
  );
}

export default Account;
