import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import styles from './Header.module.css';
import Logo from '../../images/Logo.png';
import FeatherIcon from '../../images/feather.png';
import DropdownMenu from '../DropdownMenu/DropdownMenu';
import DropdownYear from '../DropdownYear/DropdownYear';
import AddBookModal from '../Modals/AddBook/AddBookModal';
import LoginModal from '../Modals/Login/LoginModal';
import RegisterModal from '../Modals/Register/RegisterModal';

function Header() {
  const [showAddBook, setShowAddBook] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);



  const categories = ['Amour', 'Aventure', 'Fantastique', 'Fantasy', 'Historique', 'Policier', 'Science-Fiction'];

  const handleCategorySelect = (category) => {
    // Logique pour rediriger vers la catÃ©gorie choisie
  };

  return (
    <header className={styles.Header}>
      <div className={styles.container}>
        <img className={styles.logo} src={Logo} alt="logo WonderBook" />
        <nav className={styles.navBar}>
          <ul>
            <li>
              <NavLink to="/" end className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
                Accueil
              </NavLink>
            </li>
            <li>
              <DropdownMenu categories={categories} onSelectCategory={handleCategorySelect} />
            </li>
            <li>
              <DropdownYear />
            </li>
            <li>
              <NavLink to="/Forum" className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
                Forum
              </NavLink>
            </li>
            <li>
              <NavLink to="/Collection" className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
                Ma collection
              </NavLink>
            </li>
            <li>
              <button
                type="button"
                className={styles.Button}
                aria-label="Ajouter un livre"
                onClick={() => setShowAddBook(true)}
              >
                Ajouter un livre
                <img src={FeatherIcon} alt="Feather Icon" className={styles.icon} />
              </button>
            </li>
          </ul>
        </nav>
        <div className={styles.content}>
          <div>
            <button
              type="button"
              className={styles.Button}
              aria-label="Se connecter"
              onClick={() => setShowLogin(true)}
            >
              Se connecter
              <img src={FeatherIcon} alt="Feather Icon" className={styles.icon} />
            </button>
            <button
              type="button"
              className={styles.Button}
              aria-label="Inscription"
              onClick={() => setShowRegister(true)}
            >
              Inscription
              <img src={FeatherIcon} alt="Feather Icon" className={styles.icon} />
            </button>
          </div>
          <div className={styles.searchBar}>
            <div className={styles.inputSearch}>
              <input type="text" />
              <button type="button">
                <FontAwesomeIcon icon={faMagnifyingGlass} />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Affichage des modals en fonction des Ã©tats */}
      {showAddBook && <AddBookModal onClose={() => setShowAddBook(false)} />}
      {showLogin && <LoginModal onClose={() => setShowLogin(false)} />}
      {showRegister && <RegisterModal onClose={() => setShowRegister(false)} />}
    </header>
  );
}

Header.propTypes = {
  user: PropTypes.shape({
    userId: PropTypes.string,
    token: PropTypes.string,
  }),
};

Header.defaultProps = {
  user: null,
};

export default Header;
