import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { NavLink, useNavigate } from 'react-router-dom';
import { API_ROUTES, APP_ROUTES } from '../../utils/constants';
import { useUser } from '../../lib/customHooks';
import { storeInLocalStorage } from '../../lib/common';
import styles from './Login.module.css';

function Login({ setUser, onClose }) {
  const navigate = useNavigate();
  const { user, authenticated } = useUser();

  useEffect(() => {
    if (user || authenticated) {
      navigate(APP_ROUTES.DASHBOARD);
    }
  }, [user, authenticated, navigate]);

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [notification, setNotification] = useState({ error: false, message: '' });

  const handleClose = () => {
    if (onClose) {
      onClose();
    } else {
      navigate('/');
    }
  };

  const handleClickOutside = (event) => {
    if (event.target.classList.contains(styles.modalBackground)) {
      handleClose();
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Escape') {
      handleClose();
    }
  };

  const login = async () => {
    try {
      setIsLoading(true);
      const response = await axios.post(API_ROUTES.LOGIN, { email, password });
      if (!response?.data?.token) {
        setNotification({ error: true, message: 'Une erreur est survenue' });
      } else {
        storeInLocalStorage(response.data.token, response.data.userId);
        setUser(response.data);
        handleClose();
      }
    } catch (err) {
      setNotification({ error: true, message: err.message });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div
      className={styles.modalBackground}
      onClick={handleClickOutside}
      role="button"
      tabIndex={0}
      onKeyDown={handleKeyDown}
    >
      <div className={`${styles.notification} ${notification.error ? styles.errorMessage : ''}`}>
        {notification.message.length > 0 && <p>{notification.message}</p>}
      </div>

      <div className={styles.modalContent}>
        <h2>S&apos;identifier</h2>

        <label htmlFor="email">
          <p>E-mail</p>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>

        <label htmlFor="password">
          <p>Mot de passe</p>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>

        <div className={styles.option}>
          <NavLink to="/ForgetPassword">Mot de passe oublié ?</NavLink>
          <p>ou</p>
          <NavLink to="/Register">Créer un compte</NavLink>
        </div>

        <div className={styles.buttonContainer}>
          <button type="button" className={styles.cancelButton} onClick={handleClose}>Annuler</button>
          <button type="button" className={styles.validateButton} onClick={login} disabled={isLoading}>
            {isLoading ? 'Chargement...' : 'Valider'}
          </button>
        </div>
      </div>
    </div>
  );
}

Login.propTypes = {
  setUser: PropTypes.func.isRequired,
  onClose: PropTypes.func,
};

Login.defaultProps = {
  onClose: null,
};

export default Login;
