import React, { useState } from 'react';
import Banner from '../../images/library.png';
import Avatar from '../../images/avatar.png';
import FeatherIcon from '../../images/feather.png';
import styles from './Account.module.css';

function Account() {
  const backgroundImageStyle = { backgroundImage: `url(${Banner})` };
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [isEditing, setIsEditing] = useState(false); // Active/désactive l'édition

  return (
    <div className={styles.account}>
      <div className={styles.banner} style={backgroundImageStyle} />
      <main className={styles.main}>
        <button
          type="button"
          className={styles.Button}
          aria-label="edit"
          onClick={() => setIsEditing(!isEditing)} // Active/désactive les champs
        >
          Editer Profile
          <img src={FeatherIcon} alt="Feather Icon" className={styles.icon} />
        </button>

        <section className={styles.info}>
          <article>
            <label htmlFor="username">
              <p>Pseudo</p>
              <input
                type="text"
                name="username"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                disabled={!isEditing}
              />
            </label>

            <label htmlFor="email">
              <p>E-mail</p>
              <input
                type="email"
                name="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                disabled={!isEditing}
              />
            </label>
          </article>
          <aside>
            <label
              htmlFor="attachment"
              className={`${styles.ImageUpload} ${!isEditing ? styles.disabled : ''}`}
            >
              <div className={styles.ImageContainer}>
                <img src={Avatar} alt="Upload Icon" className={styles.UploadIcon} />
                <p className={styles.FormatText}>Formats d’image acceptés</p>
                <p className={styles.FormatTypes}>PNG, JPEG, WEBP</p>
              </div>
              <input
                type="file"
                id="attachment"
                className={styles.FileInput}
                accept=".png, .jpeg, .jpg, .webp"
                disabled={!isEditing}
              />
            </label>
          </aside>
        </section>
      </main>
    </div>
  );
}

export default Account;
