import React, { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import SignIn from './Modals/SignIn/SignIn';
import Login from './Modals/Login/Login';
import Forget from './Modals/Forget/Forget';
import ForgetSend from './Modals/ForgetSend/ForgetSend';
import RegisterSend from './Modals/RegisterSend/RegisterSend';
import Home from './pages/Home/Home';
import Book from './pages/Book/Book';
import { APP_ROUTES } from './utils/constants';
import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import AddBook from './Modals/AddBook/AddBook';
import UpdateBook from './pages/updateBook/UpdateBook';
import { useUser } from './lib/customHooks';
import Forum from './pages/Forum/Forum';
import Account from './pages/Account/Account';
import ChangePass from './Modals/ChangePass/ChangePass';
import DropdownMenu from './components/DropdownMenu/DropdownMenu';
import DropdownYear from './components/DropdownYear/DropdownYear';

function App() {
  const [user, setUser] = useState(null);
  const { connectedUser } = useUser();
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedYear, setSelectedYear] = useState(null);
  const [openLogin, setOpenLogin] = useState(false);
  const [openRegister, setOpenRegister] = useState(false);
  const [openForgetPassword, setOpenForgetPassword] = useState(false);
  const [openAddBook, setOpenAddBook] = useState(false);
  const [openChangePass, setOpenChangePass] = useState(false);

  useEffect(() => {
    setUser(connectedUser);
  }, [connectedUser]);
  return (
    <BrowserRouter>
      <Header user={user} setUser={setUser} />
      <nav>
        <DropdownMenu
          categories={['Amour', 'Aventure', 'Fantastique', 'Fantasy', 'Historique', 'Policier', 'Science-Fiction']}
          onFilterChange={setSelectedCategories}
        />
        <DropdownMenu
          categories={['Amour', 'Aventure', 'Fantastique', 'Fantasy', 'Historique', 'Policier', 'Science-Fiction']}
          onFilterChange={(categories) => setSelectedCategories(categories || [])}
        />
        <DropdownYear
          onFilterChange={(year) => setSelectedYear(year || '')}
        />
      </nav>
      <Routes>
        <Route
          index
          element={(
            <Home
              selectedCategories={selectedCategories}
              selectedYear={selectedYear}
            />
          )}
        />

        <Route path={APP_ROUTES.FORGET_SEND} element={<ForgetSend setUser={setUser} />} />
        <Route path={APP_ROUTES.REGISTER_SEND} element={<RegisterSend setUser={setUser} />} />
        <Route path={APP_ROUTES.FORUM} element={<Forum />} />
        <Route path={APP_ROUTES.BOOK} element={<Book />} />
        <Route path={APP_ROUTES.UPDATE_BOOK} element={<UpdateBook />} />
        <Route path={APP_ROUTES.ADD_BOOK} element={<AddBook />} />
        <Route path={APP_ROUTES.ACCOUNT} element={<Account />} />
        <Route path={APP_ROUTES.CHANGE_PASS} element={<ChangePass />} />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
}

export default App;
