import React, { useState, useEffect, useRef } from 'react';
import { NavLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import styles from './DropdownMenu.module.css';

function DropdownMenu({ categories, backgroundClass }) {
  const [isOpen, setIsOpen] = useState(false);
  const [filterType, setFilterType] = useState('et');
  const dropdownRef = useRef(null); // Référence pour détecter les clics externes

  const toggleDropdown = () => setIsOpen(!isOpen);

  // Fermer le menu si on clique en dehors
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    // Ajouter l'écouteur d'événement lorsqu'on ouvre le menu
    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    // Nettoyer l'événement lorsque le menu est fermé
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen]);

  return (
    <div className={styles.dropdownContainer} ref={dropdownRef}>
      <button className={styles.dropdownButton} type="button" onClick={toggleDropdown}>
        Catégories
      </button>

      {isOpen && (
        <div className={`${styles.dropdownMenu} ${styles[backgroundClass]}`}>
          <div className={styles.filterSection}>
            <span>Filtrage</span>
            <div className={styles.filterToggle}>
              <label h>
                <input
                  type="checkbox"
                  name="filter"
                  value="et"
                  checked={filterType === 'et'}
                  onChange={() => setFilterType(filterType === 'et' ? null : 'et')}
                />
                et
              </label>
              <label htmlFor="filterType">
                <input
                  type="checkbox"
                  name="filter"
                  value="ou"
                  checked={filterType === 'ou'}
                  onChange={() => setFilterType(filterType === 'ou' ? null : 'ou')}
                />
                ou
              </label>
            </div>
          </div>

          <ul className={styles.categoryList}>
            {categories.map((category) => (
              <li key={category} className={styles.categoryItem}>
                <NavLink to={`/category/${category.toLowerCase()}`} className={styles.linkItem}>
                  {category}
                </NavLink>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

DropdownMenu.propTypes = {
  categories: PropTypes.arrayOf(PropTypes.string).isRequired,
  backgroundClass: PropTypes.string,
};

DropdownMenu.defaultProps = {
  backgroundClass: '',
};

export default DropdownMenu;
