import React from 'react';
import PropTypes from 'prop-types';
import styles from './Modal.module.css';

function Modal({ isOpen, onClose, children }) {
  if (!isOpen) return null;

  const handleBackdropClick = (e) => {
    if (e.target.classList.contains(styles.ModalBackdrop)) {
      onClose(); // Ferme la modal si on clique à l'extérieur
    }
  };

  return (
    <div
      className={styles.ModalBackdrop}
      onClick={handleBackdropClick}
      role="presentation"  // Correction ici : Utilisation de role="presentation" pour ignorer l'interaction clavier
    >
      <div
        className={styles.ModalContent}
        role="dialog"
        aria-modal="true"
        tabIndex={-1}  // Assure que le contenu est focusable pour les lecteurs d'écran
        onKeyDown={(e) => {
          if (e.key === 'Escape') {
            onClose(); // Permet de fermer la modal avec la touche 'Escape'
          }
        }}
      >
        <button
          type="button"
          onClick={onClose}
          className={styles.CloseButton}
          aria-label="Fermer la modal"
        >
          X
        </button>
        {children}
      </div>
    </div>
  );
}

Modal.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
  children: PropTypes.node.isRequired,
};

export default Modal;
